					<Switch>
						<div className={`${location.pathname !== '/' ? "content" : ""}`}>
							<Route exact path ='/cart' component={Cart} />
							<Route exact path ='/order' component={Order} />
						</div>
					</Switch>
			<Footer/>
		</>
	);
}

export default App;
