import React, {useState} from 'react'
// styles
import './DishesBlock.sass'
// classNames
import classNames from 'classnames'
// propTypes
import PropTypes from 'prop-types'
// components
import {Button} from '../../index'
import DishesBlockToggles from './DishesBlockToggles'

function DishesBlock({id, name, category, imageUrl, price, toggle1, toggle2, onClickAddDish, light, dark, className}) {
	const [activeToggle1, setActiveToggle1] = useState(toggle1[0])
	const availableToggle1 = ['тонке', 'традиційне']
	const [activeToggle2, setActiveToggle2] = useState(toggle2[0])
	const availableToggle2 = [25, 30, 40]

	// const onSelectToggle1 = (index) => {
	// 	setActiveToggle1(index)
	// }

	// const onSelectToggle2 = (index) => {
	// 	setActiveToggle2(index)
	// }

	const onAddDish = () => {
		const dishInfo = {
			id,
			name, 
			imageUrl,
			price,
			toggle1: availableToggle1[activeToggle1],
			toggle2: availableToggle2[activeToggle2],
		}
		onClickAddDish(dishInfo)
	}

	return (
		<div className={classNames('dish-block', className, {
			'dish-block--light': light,
			'dish-block--dark': dark,
		})}>
		<div className="dish-block__wrapper-image">
			<img
				className="dish-block__image"
				src={imageUrl}
				alt="dishes-image"
			/>
		</div>
		<h4 className="dish-block__title">{name}</h4>
		<div className="dish-block__selector">
			<ul className="dish-block__selector-items">
				{availableToggle1.map((toggle, index) => 
					<li 
						onClick={() => setActiveToggle1(index)} 
						className={ classNames("dish-block__selector-item", { 
							'active': activeToggle1 === index,
							'disabled': !toggle1.includes(index),
						})} 
						key={toggle}>
						{toggle}
					</li>)}
			</ul>
			<ul className="dish-block__selector-items">
			{availableToggle2.map((toggle, index) => 
					<li 
						onClick={() => setActiveToggle2(index)} 
						className={ classNames("dish-block__selector-item", { 
							'active': activeToggle2 === index,
							'disabled': !toggle2.includes(index),
						})} 
						key={toggle}>
						{toggle} см.
					</li>)}
			</ul>
		</div>
		<div className="dish-block__bottom">
			<div className="dish-block__price">{price} ₴</div>
			<Button onClick={onAddDish} className="" add {...light ? {light} : {dark}}>
				<svg
					width="12"
					height="12"
					viewBox="0 0 12 12"
					fill="none"
					xmlns="http://www.w3.org/2000/svg"
				>
					<path
						d="M10.8 4.8H7.2V1.2C7.2 0.5373 6.6627 0 6 0C5.3373 0 4.8 0.5373 4.8 1.2V4.8H1.2C0.5373 4.8 0 5.3373 0 6C0 6.6627 0.5373 7.2 1.2 7.2H4.8V10.8C4.8 11.4627 5.3373 12 6 12C6.6627 12 7.2 11.4627 7.2 10.8V7.2H10.8C11.4627 7.2 12 6.6627 12 6C12 5.3373 11.4627 4.8 10.8 4.8Z"
						fill="white"
					/>
				</svg>
				Додати
			</Button>
		</div>
	</div>
	)
}

DishesBlock.propTypes = {
	name: PropTypes.string.isRequired,
	imageUrl: PropTypes.string.isRequired,
	price: PropTypes.number.isRequired,
	toggle1: PropTypes.arrayOf(PropTypes.number).isRequired,
	toggle2: PropTypes.arrayOf(PropTypes.number).isRequired,
	onClickAddDish: PropTypes.func,
	addedCount: PropTypes.number,
}

DishesBlock.defaultProps = {
	name: '------',
	imageUrl: '',
	price: 0,
	toggle1: [],
	toggle2: [],
}

export default DishesBlock
