import React from 'react'
// styles
import './Cart.sass'
// components
import {Button, Title, CartItem} from '../../components'
// redux
import {clearCart, removeCartItem, plusCartItem, minusCartItem} from '../../redux/actions/cart'
import {useSelector, useDispatch} from 'react-redux'
// router
import {Link} from 'react-router-dom'
// icons
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faShoppingBasket} from '@fortawesome/free-solid-svg-icons'
// visual content
import emptyImage from '../../images/empty-cart.png'
import Basket from '../../images/basket.svg'

function Cart() {
	const {totalPrice, totalCount, items } = useSelector( ({cart}) => cart)
	const addedDishes = Object.values(items).map( dish => Object.values(dish).map( elem => Object.values(elem).map(elem => elem ) ) ) 	
	const dispatch = useDispatch()

	const onClearCart = () => {
		if(window.confirm('Очистить корзину')){
			dispatch(clearCart())
		}
	}

	const onRemoveCartItem = (id) => {
		if(window.confirm('Удалить?')){
			dispatch(removeCartItem(id))
		}
	}

	const onPlusCartItem = (id) => {
		dispatch(plusCartItem(id))
	}

	const onMinusCartItem = (id) => {
		dispatch(minusCartItem(id))
	}

	return (
		<div className="cart">
			<div className="cart__container">
				{
					totalCount ? 
					<div className="cart__content">
						<div className="cart__top-info">
							<h2 className="cart__title">
								<FontAwesomeIcon width='62px' height='62px' icon={faShoppingBasket}/>
								Корзина
							</h2>
							<Button className="cart__clear-button" solid light onClick={onClearCart}>
								<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">
									<path d="M2.5 5H4.16667H17.5" stroke="#B6B6B6" strokeWidth="1.2" strokeLinecap="round" strokeLinejoin="round"/>
									<path d="M6.66663 5.00001V3.33334C6.66663 2.89131 6.84222 2.46739 7.15478 2.15483C7.46734 1.84227 7.89127 1.66667 8.33329 1.66667H11.6666C12.1087 1.66667 12.5326 1.84227 12.8451 2.15483C13.1577 2.46739 13.3333 2.89131 13.3333 3.33334V5.00001M15.8333 5.00001V16.6667C15.8333 17.1087 15.6577 17.5326 15.3451 17.8452C15.0326 18.1577 14.6087 18.3333 14.1666 18.3333H5.83329C5.39127 18.3333 4.96734 18.1577 4.65478 17.8452C4.34222 17.5326 4.16663 17.1087 4.16663 16.6667V5.00001H15.8333Z" stroke="#B6B6B6" strokeWidth="1.2" strokeLinecap="round" strokeLinejoin="round"/>
									<path d="M8.33337 9.16667V14.1667" stroke="#B6B6B6" strokeWidth="1.2" strokeLinecap="round" strokeLinejoin="round"/>
									<path d="M11.6666 9.16667V14.1667" stroke="#B6B6B6" strokeWidth="1.2" strokeLinecap="round" strokeLinejoin="round"/>
								</svg>
								Очистить корзину
							</Button>
						</div>
						<div className="cart__list">
							{
								addedDishes.map(elem => elem.map( (elem) => <CartItem id={elem[0].id} categoryId={elem[0].categoryId} name={elem[0].name} type={elem[0].type} size={elem[0].size} count={elem.length} price={elem[0].price * elem.length} removeDish={onRemoveCartItem} plusCartItem={onPlusCartItem} minusCartItem={onMinusCartItem} ></CartItem> ))
							}
						</div>
						<div className="cart__bottom-info">
							<div className="cart__details">
								<span> Всего пицц: <b>{totalCount} шт.</b> </span>
								<span> Сумма заказа: <b>{totalPrice} ₽</b> </span>
							</div>
							<div className="cart__order-buttons">
								<Link to="/dishes" className="button__back-button">
									<Button solid light>
										<svg width="8" height="14" viewBox="0 0 8 14" fill="none" xmlns="http://www.w3.org/2000/svg">
											<path d="M7 13L1 6.93015L6.86175 1" stroke="#D3D3D3" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round"/>
										</svg>
										Вернуться назад
									</Button>
								</Link>
								<Button className='cart__pay-button' solid light>Оплатить сейчас</Button>
							</div>
						</div>
					</div>
					:
					<div className="cart-empty">
						<Title className='cart-empty__title' title='Кошик пустий' subtitle='Вірогідно всього, ви ще не обрали бажані страви. Для того, щоб замовити страви, перейдіть на сторінку "Меню"' light/>
						<img className='cart-empty__image' src={Basket} alt="Empty cart" />
						<Link to="/dishes">
							<Button className='cart-empty__image' solid light>Перейти до меню</Button>
						</Link>
					</div>
				}
			</div>
		</div>
	)
}

export default Cart
