import React, {useState} from 'react'

function Input({tag, type, placeholder, name, className, value, checked, disabled, onClick, onChange, children}) {
	let outputInput = null

	if(tag === 'input'){
		outputInput = <input type={type} placeholder={placeholder} className={className} name={name} value={value} onChange={onChange} onClick={onClick}/>
	}else if(tag === 'select'){
		outputInput = <select className={className} name={name} value={value} disabled={disabled} onChange={onChange} onClick={onClick}>{children}</select>
	}else if(tag === 'textarea'){
		outputInput = <textarea type={type} className={className} name={name} value={value} onChange={onChange} onClick={onClick}/>
	}else if(tag === 'radio'){
		outputInput = <label><input type={type} className={className} name={name} value={value} checked={checked} onChange={onChange} onClick={onClick}/>{children}</label>
	}

	return (
		outputInput
	)
}

export default Input
