import React from 'react'
import './Discount.sass'
import SwiperCore, {Navigation, Pagination, EffectFade, Autoplay} from 'swiper'
import { Swiper, SwiperSlide } from 'swiper/react'
import {Title} from '../../index'

SwiperCore.use([Navigation, Pagination, EffectFade, Autoplay])

function Discount() {
	return (
		<div className="reservation">
			<Title className='reservation__title' title='Скидки і акції' subtitle='скористайтесь найкращими пропозиціями' light/>
			<Swiper
					spaceBetween={20}
					slidesPerView={3}
					loop={true}
					pagination={{ clickable: true }}
					draggable={false}
					autoplay={{ delay: 1500 }}
				>
					{
						deliveryDishes.map(elem =>{
							return <SwiperSlide>
								<DishesBlock onClickAddDish={handleAddDishToCart} id={elem.id} name={elem.name} imageUrl={elem.imageUrl} price={elem.price} types={elem.types} sizes={elem.sizes} dark/>
							</SwiperSlide>
						})
					}	
				</Swiper>
		</div>
	)
}

export default Discount
