import React, {useState} from 'react'
import './Order.sass'
import {BrowserRouter as Router, Switch, Route, useHistory, Link, useRouteMatch} from 'react-router-dom'
import {Map} from '../../components'

function Order() {
	const [street, setStreet] = useState('')
	const [house, setHouse] = useState('')
	const [apartment, setApartment] = useState('')
	const [frontDoor, setFrontDoor] = useState('')
	const [details, setDetails] = useState('')

	const valueToState = (e) => {
		e.persist()
		const name = e.target.name
		const value = e.target.value

		switch(name){
			case 'street':
				setStreet(value)
				break
			case 'house':
				setHouse(value)
				break
			case 'apartment':
				setApartment(value)
				break
			case 'frontDoor':
				setFrontDoor(value)
				break
			case 'details':
				setDetails(value)
				break
			default:
				break
		}
	}


	const submitForm = (e) => {
		e.preventDefault()
		e.persist()
		console.log()
	}

	return (
		<div className="order">
			<div className="order__container">
				<details className="order__destination" style={{color:'white'}}>
					<summary>Section 1</summary>
					<div className="order__step-info" style={{height:'300px'}}>
						<form onSubmit={submitForm}>
							<input type="text" name='street' placeholder='Вулиця' value={street} onChange={valueToState}/>
							<input type="text" name='house' placeholder='Дім' value={house} onChange={valueToState}/>
							<input type="text" name='apartment' placeholder='Квартира' value={apartment} onChange={valueToState}/>
							<select id="" name="frontDoor" value={frontDoor} onChange={valueToState}>
								<option value="" disabled selected>Парадна</option>
								<option value="1">1</option>
								<option value="2">2</option>
								<option value="3">3</option>
								<option value="4">4</option>
								<option value="5">5</option>
								<option value="6">6</option>
							</select>
							<textarea name="details" id="" cols="30" rows="10" placeholder='Коментар до адреси' value={details} onChange={valueToState}></textarea>
							<div className="">
								<label>
									<input type="radio" class="" name="delivery" checked />
									Вийду до машини
								</label>
								<label>
									<input type="radio" class="" name="delivery" />
									До дверей
								</label>
							</div>
							<button>Знайти на карті</button>
						</form>
					<Map />
					</div>
				</details>
				
			</div>
		</div>
	)
}

export default Order

















