import React, {useState, useEffect} from 'react'
import './Order.sass'
import {Map, Input, Button} from '../../components'
import {useForm} from 'react-hook-form'

function Destination({checkValid}) {
	const {register, handleSubmit, errors} = useForm()

	const [destinationAddress, setDestinationAddress] = useState('')

	const submitForm = data => {
		// e.preventDefault()
		console.log(data)
		// return (name && telephone && street && house) ? setDestinationAddress(`Одеса,${street},${house}`) : null 		
	}

	return (
		<div className='destination'>
			<form onSubmit={handleSubmit(submitForm)} className='destination__form'>
				<input type="text"
				tag='input' 
				name='name' 
				placeholder='Ім`я' 
				className={`destination__name ${errors.name ? 'input__error' : ''}`} 
				ref={register({required: true})} />
				{/* <Input 
					tag='input' 
					type="text" 
					name='name' 
					placeholder='Ім`я' 
					className={`destination__name ${errors.name ? 'input__error' : ''}`} 
					ref={register({required: true})} 
					solid 
					light/> */}
				<Input 
					tag='tel' 
					type="tel" 
					name='telephone' 
					placeholder='Номер телефону' 
					className="destination__telephone" 
					ref={register({required: 'required'})} 
					solid 
					light/>
				{/* <Input tag='input' type="text" name='street' placeholder='Вулиця' className="destination__street" solid light/>
				<Input tag='input' type="text" name='house' placeholder='Дім' className="destination__house" solid light/>
				<Input tag='input' type="number" min="1" step="1" name='apartment' placeholder='Квартира' className="destination__apartment" solid light/>
				<Input tag='select' name='frontDoor' className="destination__frontDoor" disabled={apartment ? false : true} solid light>
					<option value="" disabled selected>Парадна</option>
					<option value=""></option>
					<option value="1">1</option>
					<option value="2">2</option>
					<option value="3">3</option>
					<option value="4">4</option>
					<option value="5">5</option>
					<option value="6">6</option>
				</Input>
				<div className="destination__reception">
					<Input tag='radio' type="radio" name="delivery" checked={delivery === 'toCar'} solid light>Вийду до машини</Input>
					<Input tag='radio' type="radio" name="delivery" checked={delivery === 'toDoors'} solid light>До дверей</Input>
				</div>
				<Input tag='textarea' name="details" cols="30" rows="5" placeholder='Коментар до адреси...' className="destination__details" solid light/> */}
				<input type="submit"/>
				{/* <Button className="destination__submit" solid light>Знайти місцезнаходження на карті</Button> */}
			</form>
			<Map className="destination__map" address={destinationAddress}/>
		</div>
	)
}

