import React, {useState, memo} from 'react'
// styles
import './Categories.sass'
import PropTypes from 'prop-types'

const Categories = memo(function Categories({activeCategory, items, onClickItem, selectCategory}) {
	return (
		<div className="categories">
			<ul className="categories__list">
				<li className={activeCategory === null ? "categories__item--active" : ""} onClick={() => selectCategory(null)}>Все</li>
				{items && items.map( (category, index) => {
					return <li className={index === activeCategory ? "categories__item--active" : ''} onClick={() => selectCategory(category.category)} key={`${category.title}_${index}`}>{category.title}</li>
				})}
			</ul>
		</div>
	)
})

Categories.propTypes = {
	activeCategory: PropTypes.number,
	items: PropTypes.arrayOf(PropTypes.string).isRequired,
	onClickItem: PropTypes.func,
}

Categories.defaultProps = {
	activeCategory: null,
	items: [],
}

export default Categories
