import React from 'react'
import './Delivery.sass'
import SwiperCore, {Navigation, Pagination, EffectFade, Autoplay} from 'swiper'
import { Swiper, SwiperSlide } from 'swiper/react'
import {Title, Button} from '../../../components'

SwiperCore.use([Navigation, Pagination, EffectFade, Autoplay])

function Delivery() {
	return (
		<div className='delivery'>
			<div className="delivery__description">
				<div className="delivery__title">Заголовок доставки</div>
				<div className="delivery__subtitle">Описание доставки</div>
				<Button className='button__skeleton' dark>
					Доставка	
				</Button>
			</div>
			<div className="delivery__slider">
				<Swiper
				// effect="fade"
				spaceBetween={50}
				slidesPerView={3}
				loop={true}
				pagination={{ clickable: true }}
				draggable={false}
				autoplay={{ delay: 2000 }}
				>
					{
						mainSlides.map( (dish, index) => {
							console.log(dish.image)
							return <SwiperSlide style={{backgroundImage:`url(${Object.values(dish.image)[0]})`}}>
										<Link to='/dishes'>
											<div className='first-slider__slide-title'>{dish.title}</div>
										</Link>
									</SwiperSlide>
						})
					}
				</Swiper>
			</div>
		</div>
	)
}

export default Delivery
