import React, {useState, useEffect} from 'react'
import {Map, Input, Button} from '../../components'
import {useForm} from 'react-hook-form'
import {useSelector, useDispatch} from 'react-redux'
import {} from ''
// styles
import './Order.sass'
// components

const defaultValues = {
	delivery: "toCar",
	frontDoor: ""
}


function Order() {
	// const otderItems = useSelector(state => )
	// const [validClientInfo, setValidClientInfo] = useState(false)
	const {totalPrice} = useSelector( ({cart}) => cart)
	const {register, handleSubmit, getValues, setValues, watch, formState: { isValid, isDirty}, errors} = useForm({ defaultValues })
	const [destinationAddress, setDestinationAddress] = useState('')

	const watchApartments = watch("apartment", false)
	const watchStreet = watch("street")
	const watchHouse = watch("house")

	const submitForm = data => {
		// e.preventDefault()
		console.log(watchApartments)
		console.log(data)
		// return isValid ? setDestinationAddress(`Одеса,${data.street},${data.house}`) : null
		// return (name && telephone && street && house) ? setDestinationAddress(`Одеса,${street},${house}`) : null 		
	}

	const findDestinationMarker = (data) => {
		return watchStreet && watchHouse ? setDestinationAddress(`Одеса,${watchStreet},${watchHouse}`) : null
	}

	return (
		<div className="order">
			<div className="order__container">
				<div className="order__info-block">
				<div className='destination'>
			<form id="order-sent-form" className='destination__form'>

				<input 
					type="text"
					name='name' 
					placeholder='Ім`я' 
					className={`input input__solid input__light destination__name ${errors.name ? 'input__error' : ''}`} 
					ref={register({required: true})}
				/>
				<input 
					type="tel"
					name='telephone' 
					placeholder='Номер телефону' 
					className={`input input__solid input__light destination__telephone ${errors.telephone ? 'input__error' : ''}`} 
					ref={register({required: true})}
				/>
				<input 
					type="text" 
					name='street' 
					placeholder='Вулиця' 
					className={`input input__solid input__light destination__street ${errors.street ? 'input__error' : ''}`}
					ref={register({required: true})}
				/>
				<input 
					type="text" 
					name='house' 
					placeholder='Дім' 
					className={`input input__solid input__light destination__house ${errors.house ? 'input__error' : ''}`}
					ref={register({required: true})}
				/>
				<input 
					type="number" 
					name="apartment" 
					placeholder='Квартира' 
					className={`input input__solid input__light destination__apartment ${errors.apartment ? 'input__error' : ''}`}
					ref={register({required: false})}
				/>
				<select 
					name='frontDoor' 
					className={`input input__solid input__light destination__frontDoor ${errors.frontDoor ? 'input__error' : ''}`}
					disabled={getValues("apartment") ? false : true} 
					ref={register({required: watchApartments ? true : false})}
				>
					<option value="" disabled selected={watchApartments ? false : true} >Парадна</option>
					<option value="1">1</option>
					<option value="2">2</option>
					<option value="3">3</option>
					<option value="4">4</option>
					<option value="5">5</option>
					<option value="6">6</option>
				</select>


				<div className="destination__reception">
					<label 
						className={`label label--light`}
					>
						<input 
							type="radio"
							className={`input`} 
							name="delivery" 
							value="toCar"
							defaultChecked
							ref={register({required: false})}
						/>
						Вийду до машини
					</label>
					<label 
						className={`label label--light`}
					>
						<input 
							type="radio"
							className={`input`} 
							name="delivery"
							value="toDoors"
							ref={register({required: false})}
						/>
						До дверей
					</label>
				</div>



			<textarea
				name="details" 
				cols="30" 
				rows="5" 
				placeholder="Коментар до адреси..." 
				className={`input input__solid input__light destination__details`}
				ref={register({required: false})}
			/> 
				<Button type="button" onClick= {findDestinationMarker} className="destination__submit" solid light>Знайти місцезнаходження на карті</Button>
			</form>
			<Map  className="destination__map" address={destinationAddress}/>
		</div>


		{/* <div className="cart__list">
							{
								addedDishes.map(elem => elem.map( (elem) => <CartItem id={elem[0].id} categoryId={elem[0].categoryId} name={elem[0].name} image={elem[0].imageUrl} type={elem[0].type} size={elem[0].size} count={elem.length} price={elem[0].price * elem.length} removeDish={onRemoveCartItem} plusCartItem={onPlusCartItem} minusCartItem={onMinusCartItem} ></CartItem> ))
							}
						</div> */}


		<div className="details">
			<div className="details__price" style={{color:'white'}}>Загальний рахунок: <span>{totalPrice}</span></div>
			<Button form="order-sent-form" onClick={handleSubmit(submitForm)} type='submit' className="order__submit" solid light>Замовити</Button>
		</div>

					{/* <Destination checkValid={setValidClientInfo}/>
					<Details isValid={validClientInfo}/> */}
				</div>
			</div>
		</div>
	)
}

export default Order










