import React, {useState} from 'react'

function DishesBlockToggles({category, toggle1, toggle2, setToggle1, setToggle2}) {
	const [activeToggle1, setActiveToggle1] = useState(toggle1[0])
	const availableToggle1 = ['тонке', 'традиційне']
	const [activeToggle2, setActiveToggle2] = useState(toggle2[0])
	const availableToggle2 = [25, 30, 40]

	const onSelectToggle1 = (index) => {
		setActiveToggle1(index)
	}

	const onSelectToggle2 = (index) => {
		setActiveToggle2(index)
	}

	return (
		<>
			{ 
				category === 'pizza' ?
					<>
						<ul className="dish-block__selector-items">
							{availableToggle1.map((toggle, index) => 
								<li 
									onClick={() => onSelectToggle1(index), setToggle1(availableToggle1[index])} 
									className={ classNames("dish-block__selector-item", { 
										'active': activeToggle1 === index,
										'disabled': !toggle1.includes(index),
									})} 
									key={toggle}>
									{toggle}
								</li>)}
						</ul>
						<ul className="dish-block__selector-items">
						{availableToggle2.map((toggle, index) => 
								<li 
									onClick={() => onSelectToggle2(index), setToggle2(availableToggle2[index])} 
									className={ classNames("dish-block__selector-item", { 
										'active': activeToggle2 === index,
										'disabled': !toggle2.includes(index),
									})} 
									key={toggle}>
									{toggle} см.
								</li>)}
						</ul>
					</>
				: category === 'drink' ?
					<>
						<ul className="dish-block__selector-items">
						{availableToggle1.map((toggle, index) => 
							<li 
								onClick={() => onSelectToggle1(index)} 
								className={ classNames("dish-block__selector-item", { 
									'active': activeToggle1 === index,
									'disabled': !toggle1.includes(index),
								})} 
								key={toggle}>
								{toggle}
							</li>)}
						</ul>
					</>
				: null
			}
		</>
	)
}

export default DishesBlockToggles
