import React, {useState} from 'react'

function Input({tag, type, placeholder, name, className, value, checked, onClick, onChange, children}) {
	const [outputInput, setOutputInput] = useState(null)
{
	if(tag === 'input'){
		setOutputInput(<input type={type} placeholder={placeholder} className={className} name={name} value={value} onChange={onChange} onClick={onClick}/>)
	}else if(tag === 'select'){
		setOutputInput(<select type={type} className={className} name={name} value={value} onChange={onChange} onClick={onClick}>{children}</select>)
	}else if(tag === 'textarea'){
		setOutputInput(<textarea type={type} className={className} name={name} value={value} onChange={onChange} onClick={onClick}/>)
	}else if(tag === 'radio'){
